import React, { useEffect, useState } from "react";
import Recipe from './recipe';
import "./App.css";


const App = () => {
  const APP_ID = "16461e2f";
  const APP_KEY = "59aa56f15f181ad7fe969e446a84a60c";

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('');

  //Making an HTTP request and using Aync await to handle promises.
  const getRecipes = async () => {
    const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`);
    const data = await response.json();
    setRecipes(data.hits);
    console.log(data.hits);
  };

  useEffect(() => {
    console.log("Effect Ran");
    getRecipes();
  }, [query]);//Leaving this empty allows useEffect to run only when the application mounts.



  const updateSearch = e =>{
    setSearch(e.target.value);
    console.log(search);
  }

  const getSearch = e =>{
    e.preventDefault();
    setQuery(search);
    setSearch('');
  }

  return (
    <div className="container">
      <img src="../recipeapplogo.png" alt="Recipe app logo" height="300px" width="300px" ></img>
      <h1 className="display-4 mb-4">Recipe Sandbox</h1>
      <hr/>
      <form  onSubmit={getSearch} className="search-form">
        <div className="form-group">
          <input className="form-control" type="text" value={search} onChange={updateSearch}></input>
        </div>
      <div className="form-group">
        <button  className="btn btn-primary mr-4" type="submit">
          search
        </button>
      </div>
      </form>
      {recipes.map(recipe =>(
        <Recipe 
        key = {recipe.recipe.label}
        title = {recipe.recipe.label}
        calories = {recipe.recipe.calories}
        image = {recipe.recipe.image}
        ingredients = {recipe.recipe.ingredients}/>

      ))}
    </div>
  );
};

export default App;
